import React, { useState } from "react";

const Navbar = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const links = [
    {
      href: "./add_resume.html",
      label: "Составить резюме",
    },
    {
      href: "./projects.html",
      label: "Шаблоны",
    },
    {
      href: "./about.html",
      label: "О сайте",
    },
    {
      href: "./auth.html",
      label: "Войти",
    },
  ];

  const handleClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <nav className="nav">
      <div className="container">
        <div className="nav-row">
          <a href="./index.html" className="logo">
            GO WORK
          </a>
          <div className="nav-wrapper" style={{ position: "relative" }}>
            <ul className="nav-list">
              {links.map((link, index) => (
                <li
                  key={index}
                  className="nav-list__item"
                  style={{ position: "relative" }}
                >
                  <a
                    href={link.href}
                    className={`nav-list__link ${
                      activeIndex === index ? "nav-list__link--active" : ""
                    }`}
                    onClick={(e) => {
                      e.preventDefault();
                      handleClick(index);
                    }}
                  >
                    {link.label}
                  </a>
                </li>
              ))}
            </ul>
            {/* Перемещающаяся линия */}
            <div
              className="nav-indicator"
              style={{
                position: "absolute",
                bottom: 0,
                height: "2px",
                backgroundColor: "var(--accent)", // или любой цвет
                width: "150px", // ширина пункта, подстроить под стиль
                transition: "transform 0.3s ease",
                transform: `translateX(${activeIndex * 150}px)`, // перемещение
              }}
            />
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
