import React, { useState } from "react";

const Navbar = () => {
  const [activeLink, setActiveLink] = useState("/");

  const links = [
    { href: "./Creater.js", label: "Составить резюме" },
    { href: "./projects.html", label: "Шаблоны" },
    { href: "./about.html", label: "О сайте" },
    { href: "./auth.html", label: "Войти" },
  ];

  const handleClick = (href) => {
    setActiveLink(href);
  };

  return (
    <nav className="nav">
      <div className="container">
        <div className="nav-row">
          <a href="./index.html" className="logo">
            GO WORK
          </a>
          <ul className="nav-list">
            {links.map((link) => (
              <li key={link.href} className="nav-list__item">
                <a
                  href={link.href}
                  className={`nav-list__link ${
                    activeLink === link.href ? "nav-list__link--active" : ""
                  }`}
                  onClick={(e) => {
                    e.preventDefault();
                    handleClick(link.href);
                  }}
                >
                  {link.label}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
